def get_ticker_from_company_number(company_number: int):
    conn = connect_to_db()
    cursor = conn.cursor()
    try:
        cursor.execute("SELECT ticker FROM company_overview WHERE company_number = %s", (company_number,))
        ticker = cursor.fetchone()
        return ticker[0] if ticker else None
    finally:
        cursor.close()
        conn.close()
